pragma solidity 0.8.10;

library Ad {
	uint256 private constant _ID_LENGTH = 100000000000000000000000000000000;
	// 0.RRP     : Recommended Retail Price
	// 1.DUTCH   : Dutch Auction
	// 2.ENGLISH : English Acction
	// 3.OFFER   : Offered by Others
	// 4.OPEN    : Open Bid by revealing the content
	enum Pricing {
		RRP,
		DUTCH,
		ENGLISH,
		OFFER,
		OPEN
	}
	struct Period {
		address mediaProxy;
		string spaceMetadata;
		string tokenMetadata;
		uint256 saleStartTimestamp;
		uint256 saleEndTimestamp;
		uint256 displayStartTimestamp;
		uint256 displayEndTimestamp;
		Pricing pricing;
		uint256 minPrice;
		uint256 startPrice;
		bool sold;
	}

	function id(
		string memory metadata,
		uint256 displayStartTimestamp,
		uint256 displayEndTimestamp
	) public pure returns (uint256) {
		return
			uint256(
				keccak256(
					abi.encodePacked(metadata, displayStartTimestamp, displayEndTimestamp)
				)
			) % _ID_LENGTH;
	}
}
","keccak256":"0x47955874c274fa881de288b6af7d7ea36130caf1d64c6e367316278f6e95fd07","license":"UNLICENSED"},"contracts/libraries/Sale.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.10;

import "./Ad.sol";

library Sale {
	struct Bidding {
		uint256 tokenId;
		address bidder;
		uint256 price;
	}
	struct Offer {
		string spaceMetadata;
		uint256 displayStartTimestamp;
		uint256 displayEndTimestamp;
		address sender;
		uint256 price;
	}
	struct OpenBid {
		uint256 tokenId;
		address sender;
		uint256 price;
		string content;
	}

	function startPrice(Ad.Period memory period) public pure returns (uint256) {
		if (period.pricing == Ad.Pricing.RRP) {
			return period.minPrice;
		} else if (period.pricing == Ad.Pricing.DUTCH) {
			return period.minPrice * 10;
		} else if (period.pricing == Ad.Pricing.ENGLISH) {
			return period.minPrice;
		} else if (period.pricing == Ad.Pricing.OFFER) {
			return period.minPrice;
		} else if (period.pricing == Ad.Pricing.OPEN) {
			return period.minPrice;
		} else {
			return 0;
		}
	}
}
