pragma solidity 0.8.10;

import "../proxies/MediaProxy.sol";
import "../accessors/NameAccessor.sol";
import "../interfaces/IEventEmitter.sol";
import "../interfaces/IMediaRegistry.sol";

/// @title MediaFactory - creates proxy contracts for media accounts.
/// @author Shumpei Koike - <shumpei.koike@bridges.inc>
contract MediaFactory is NameAccessor {
	/// @dev Emitted when a new media is created.
	event CreateProxy(MediaProxy proxy);

	/// @dev Initializer
	/// @param _nameRegistry address of NameRegistry
	constructor(address _nameRegistry) {
		initialize(_nameRegistry);
	}

	/// @dev Create a new account for media. Each proxy has its own storage and saves
	///      data related to NFTs.
	/// @param mediaEOA address of the media EOA
	/// @param applicationMetadata string of constant metadata for the defailts of the account
	/// @param updatableMetadata string of constant metadata for the defailts of the account
	/// @param initializer bytes of the initalize calldata
	/// @param saltNonce uint256 of the salt number to create a proxy address
	function newMedia(
		address mediaEOA,
		string memory applicationMetadata,
		string memory updatableMetadata,
		bytes memory initializer,
		uint256 saltNonce
	) external onlyOwner returns (MediaProxy proxy) {
		proxy = createProxyWithNonce(nameRegistryAddress(), initializer, saltNonce);
		_registry().addMedia(
			address(proxy),
			applicationMetadata,
			updatableMetadata,
			mediaEOA
		);
		_event().emitNewMedia(
			address(proxy),
			mediaEOA,
			applicationMetadata,
			updatableMetadata,
			saltNonce
		);
	}

	/// @dev Allows to create new proxy contact and execute a message call to the new proxy within one transaction.
	/// @param accessor Address of accessor contract.
	/// @param initializer Payload for message call sent to new proxy contract.
	/// @param saltNonce Nonce that will be used to generate the salt to calculate the address of the new proxy contract.
	function createProxyWithNonce(
		address accessor,
		bytes memory initializer,
		uint256 saltNonce
	) internal returns (MediaProxy proxy) {
		proxy = deployProxyWithNonce(accessor, initializer, saltNonce);
		if (initializer.length > 0)
			// solhint-disable-next-line no-inline-assembly
			assembly {
				if eq(
					call(
						gas(),
						proxy,
						0,
						add(initializer, 0x20),
						mload(initializer),
						0,
						0
					),
					0
				) {
					revert(0, 0)
				}
			}
		emit CreateProxy(proxy);
	}

	/// @dev Allows to create new proxy contact using CREATE2 but it doesn't run the initializer.
	///      This method is only meant as an utility to be called from other methods
	/// @param accessor Address of accessor contract.
	/// @param initializer Payload for message call sent to new proxy contract.
	/// @param saltNonce Nonce that will be used to generate the salt to calculate the address of the new proxy contract.
	function deployProxyWithNonce(
		address accessor,
		bytes memory initializer,
		uint256 saltNonce
	) internal returns (MediaProxy proxy) {
		// If the initializer changes the proxy address should change too. Hashing the initializer data is cheaper than just concatinating it
		bytes32 salt = keccak256(
			abi.encodePacked(keccak256(initializer), saltNonce)
		);
		bytes memory deploymentData = abi.encodePacked(
			type(MediaProxy).creationCode,
			uint256(uint160(accessor))
		);
		// solhint-disable-next-line no-inline-assembly
		assembly {
			proxy := create2(
				0x0,
				add(0x20, deploymentData),
				mload(deploymentData),
				salt
			)
		}
		require(address(proxy) != address(0), "Create2 call failed");
	}

	/**
	 * Accessors
	 */
	function _registry() internal view returns (IMediaRegistry) {
		return IMediaRegistry(mediaRegistryAddress());
	}

	function _event() internal view returns (IEventEmitter) {
		return IEventEmitter(eventEmitterAddress());
	}
}
